<template>  <div :class="['cover',{'mini':rightShow}]">    <div class="inwarp">      <h1>Healen</h1>      <P>前端程序员一枚。专注于自己喜欢的事儿！</P>      <div class="btn-group">        <x-button round plain @click="toggleIndex()">          <span v-if="!rightShow">OPEN 〖  MY INFORMATION  〗</span>          <span v-else>CLOSE 〖  MY INFORMATION  〗</span>        </x-button>        <x-button round plain @click="go('https://healen.github.io/xui')">          〖  X-UI   〗        </x-button>      </div>      <div class="icongroup">        <i class="ico ico-weixin"></i>        <i class="ico ico-qq"></i>        <i class="ico ico-github"></i>      </div>    </div>  </div></template><script>  import {mapState} from 'vuex'  export default {    name: "cover",    computed:{      ...mapState([        'rightShow'      ])    },    methods:{      go(url){        window.location.href=url      },      toggleIndex(){        if(this.rightShow){          this.$router.push('/')        }else {          this.$router.push('/about')        }      }    }  }</script><style scoped lang="less">  @import "./index";</style>