<template>  <div :class="['cover',{'mini':rightShow}]">    <div class="inwarp">      <h1>Healen</h1>      <div class="btn-group">        <x-button round plain @click="gopush('/')">HOME</x-button>        <x-button round plain @click="gopush('/about')">ABOUT ME</x-button>        <x-button round plain @click="go('https://healen.github.io/xui')">HEALEN XUI</x-button>      </div>      <div class="icongroup">        <i class="ico ico-weixin"></i>        <i class="ico ico-qq"></i>        <i class="ico ico-github"></i>      </div>    </div>  </div></template><script>  import {mapState} from 'vuex'  export default {    name: "cover",    computed:{      ...mapState([        'rightShow'      ])    },    methods:{      go(url){        window.location.href=url      },      gopush(url){        this.$router.push(url)      }    }  }</script><style scoped lang="less">  @import "./index";</style>