<template>  <div class="about">    <transition name="fade">      <swiper :options="swiperOption" v-if="showbanner" ref="mySwiper" >        <swiper-slide class="sliderbox slider1">          <div class="cons">            <img :src="require('../assets/img/healen.png')" class="photo"/>            <h2>退一步海阔天空，这是一种应有的心境。</h2>            <div class="line"></div>            <p class="disa">我叫张晓东</p>            <p class="disa">前端开发攻城狮一头</p>            <p class="disa">live_web@126.com</p>            <p class="disa">&nbsp;</p>            <p class="disa">&nbsp;</p>          </div>        </swiper-slide>        <swiper-slide class="sliderbox slider2">          <div class="cons">            <h1>关于我</h1>          </div>        </swiper-slide>        <swiper-slide class="sliderbox slider3">          <div class="cons">            <h1>技能栈</h1>          </div>        </swiper-slide>        <swiper-slide class="sliderbox slider4">          <div class="cons">            <h1>经历</h1>          </div>        </swiper-slide>        <swiper-slide class="sliderbox slider5">          <div class="cons">            <h1>作品集</h1>          </div>        </swiper-slide>        <swiper-slide class="sliderbox slider6">          <div class="cons">            <h1>联系我</h1>          </div>        </swiper-slide>        <div class="swiper-pagination" slot="pagination"></div>      </swiper>    </transition>    <div class="-arrow">      <x-icon name="up"></x-icon>    </div>  </div></template><script>  import "swiper/dist/css/swiper.css"  export default {    name: "About",    data(){      return {        showbanner:false,        swiperOption: {          pagination: {            el: '.swiper-pagination',            clickable: true          },          autoplay: {            delay: 5000, //自动切换的时间间隔，单位ms            stopOnLastSlide: false, //当切换到最后一个slide时停止自动切换            disableOnInteraction:true          },          effect: 'cube',          grabCursor: true,          cubeEffect: {            shadow: true,            slideShadows: true,            shadowOffset: 20,            shadowScale: 0.94,          },          direction: 'vertical',          mousewheel : true,          speed:500,        }      }    },    created(){      setTimeout(()=>{        this.showbanner= true      },500)    },    destroyed(){      this.showbanner= false    }  }</script><style lang="less">  @import "About";</style>